css:
.mypage .p-title {
    font-size: 20px;
    font-weight: 600;
}

.mypage .letter-block {
    margin-bottom: 20px;
}

.mypage .letter-block p {
    margin: 0 0 2px;
    /*font-size: 13px;*/
}
.mypage .col-xs-1,
.mypage .col-sm-1,
.mypage .col-md-1,
.mypage .col-lg-1,
.mypage .col-xs-2,
.mypage .col-sm-2,
.mypage .col-md-2,
.mypage .col-lg-2,
.mypage .col-xs-3,
.mypage .col-sm-3,
.mypage .col-md-3,
.mypage .col-lg-3,
.mypage .col-xs-4,
.mypage .col-sm-4,
.mypage .col-md-4,
.mypage .col-lg-4,
.mypage .col-xs-5,
.mypage .col-sm-5,
.mypage .col-md-5,
.mypage .col-lg-5,
.mypage .col-xs-6,
.mypage .col-sm-6,
.mypage .col-md-6,
.mypage .col-lg-6,
.mypage .col-xs-7,
.mypage .col-sm-7,
.mypage .col-md-7,
.mypage .col-lg-7,
.mypage .col-xs-8,
.mypage .col-sm-8,
.mypage .col-md-8,
.mypage .col-lg-8,
.mypage .col-xs-9,
.mypage .col-sm-9,
.mypage .col-md-9,
.mypage .col-lg-9,
.mypage .col-xs-10,
.mypage .col-sm-10,
.mypage .col-md-10,
.mypage .col-lg-10,
.mypage .col-xs-11,
.mypage .col-sm-11,
.mypage .col-md-11,
.mypage .col-lg-11,
.mypage .col-xs-12,
.mypage .col-sm-12,
.mypage .col-md-12,
.mypage .col-lg-12 {
    padding-right: 0px;
    padding-left: 0px;
}

.mypage h3,.h3{
font-size: 14px;
}
.text{
margin-top: 85px;
padding-top:50px;}
.text h3{
font-weight: bold;  

}
.row .txt p{
font-size: 14px;}
.text p{
font-size: 11px;
padding-left:45px;
}
.mypage table,
.mypage td,
.mypage th {
	border: 1px solid #eee;
    border-collapse: collapse;
    font-size: 12px;
}

.mypage .row {
    margin-right: 0px;
    margin-left: 0px;
}

.mypage .print-format .table th {
    font-weight: 400;
}

.mypage .col-print-4 {
    width: 33.3333333%;
    float: left;
}
.print-format{
    font-size: 11px;
}
.mypage .pulled {
    margin-top: -21px !important;
}

.mypage .col-print-offset-8 {
    float: right;
    width: 33.33333333%;
}
.mypage .col-print-offset-6 {
    float: right;
    width: 50%;
}
.mypage .bordered{
	font-style: italic;
	font-size: 12px;
}

.mypage .text-left-aligned th{
	text-align: left!important;
}

.mypage .table hr{
    margin: 0px;
}

.mypage .text-left-aligned td{
    font-size: 13px;
    text-align: right!important;
}

.mypage .text-left-aligned_all td{
    font-size: 13px;
}
