.text h3{
color:#43B3BF;
font-weight: bold;  

}
.row .txt p{
font-size: 14px;}
.text p{
font-size: 11px;
padding-left:45px;
}
.mypage table,
.mypage td,
.mypage th {
    border-collapse: collapse;
    border-color: #4FD3D9 !important;
    font-size: 13px;
    text-align: center;
}

.mypage th {
    background-color: #43B3BF !important;
}

.mypage .row {
    margin-right: 0px;
    margin-left: 0px;
}

.mypage .print-format .table th {
    background-color: #43B3BF !important;
    font-weight: 400;
}

.mypage .col-print-4 {
    width: 33.3333333%;
    float: left;
}
.print-format{
    font-size: 11px;
}
.mypage .pulled {
    margin-top: -21px !important;
}

.mypage .col-print-offset-8 {
    float: right;
    width: 33.33333333%;
}
.mypage .col-print-offset-6 {
    float: right;
    width: 50%;
}
.mypage .bordered{
	border-top: 1px solid #43B3BF;
	font-style: italic;
	font-size: 12px;
	color: #43B3BF;
}
